import React from "react";
import "../Styles/MainContent.css";
import data from "../data";

class MainContent extends React.Component {
    constructor() {
        super();
        this.state = {
            people: data,
            index: 0
        };

    }

    handleNext() {
        if (this.state.i >= 24) {
          this.setState({ i: this.state.i = 0})
        } else if (this.state.i >= 0) {
          this.setState({ i: this.state.i + 1 });
        }
      }

      handlePrev() {
        if (this.state.i <= 0) {
          this.setState({ i: this.state.i = 24})
        } else if (this.state.i >= 0) {
          this.setState({ i: this.state.i - 1 });
        }
      }



    render() {

        let{people, index} = this.state;

        return (

            <div>

                <h1>{index + 1} / {people.length}</h1>

                <whiteBox>

                    <h1 class ='name'>{people[index].name.first} {people[index].name.last}</h1>

                    <h2>From: {people[index].city}, {people[index].country}</h2>
                    <h2>Job Title:{people[index].title} </h2>
                    <h2>Employer:{people[index].employer} </h2>
                    <h2>Favorite Movie:</h2>

                    <ol>
                        <li>{people[index].favoriteMovies[0]} </li>
                        <li>{people[index].favoriteMovies[1]} </li>
                        <li>{people[index].favoriteMovies[2]} </li>
                    </ol>

                </whiteBox>

                <div class="btn">
                    <button>Previous</button>
                    <button>Edit</button>
                    <button>Delete</button>
                    <button>New</button>
                    <button>Next ></button>
                </div>

            </div>


        );
    }
}

export default MainContent;
